import { useState } from "react";
import axios from "axios";
import authService from "../services/authService";

const useAuth = () => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Function to get the CSRF token from the backend
  const getCsrfToken = async () => {
    const response = await axios.get("/api/get-csrf-token", {
      withCredentials: true, // Ensure cookies are sent
    });
    return response.data.csrfToken;
  };

  const login = async (userData) => {
    try {
      const response = await authService.login(userData);
      localStorage.setItem("token", response.token); // Save JWT token
      setUser(response.user);
      setIsAuthenticated(true);
    } catch (error) {
      console.error("Login failed", error);
      setIsAuthenticated(false);
    }
  };

  const register = async (userData) => {
    try {
      const csrfToken = await getCsrfToken(); // Get CSRF token
      await authService.register(userData, csrfToken); // Pass token to authService
    } catch (error) {
      console.error("Registration failed", error);
    }
  };

  const logout = () => {
    authService.logout();
    setUser(null);
    setIsAuthenticated(false);
  };

  return { user, isAuthenticated, login, register, logout };
};

export default useAuth;
