import React, { useState } from "react";
import authService from "../services/authServices";

const EntryPage = ({ className, onClose }) => {
  const [currentView, setCurrentView] = useState("signUp");
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    name: "",
  });
  const [csrfToken, setCsrfToken] = useState(""); // Store CSRF token

  // Handle input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Fetch CSRF token on component mount
  React.useEffect(() => {
    const fetchCsrfToken = async () => {
      try {
        const response = await axios.get(
          `${process.env.REACT_APP_API_URL}/csrf-token`,
          {
            withCredentials: true, // Fetch token with credentials
          }
        );
        setCsrfToken(response.data.csrfToken);
      } catch (error) {
        console.error("Error fetching CSRF token:", error);
      }
    };

    fetchCsrfToken();
  }, []);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (currentView === "signUp") {
        await authService.register(
          {
            email: formData.email,
            password: formData.password,
            name: formData.name,
          },
          csrfToken
        );
        alert("Registration successful!");
      } else if (currentView === "logIn") {
        await authService.login(
          { email: formData.email, password: formData.password },
          csrfToken
        );
        alert("Login successful!");
      }
    } catch (error) {
      console.error("Error:", error);
      alert("An error occurred. Please try again.");
    }
  };

  const renderForm = () => {
    switch (currentView) {
      case "signUp":
        return (
          <form onSubmit={handleSubmit}>
            <h2>Sign Up</h2>
            <label>Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
            />
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <button type="submit">Sign Up</button>
            <button type="button" onClick={() => setCurrentView("logIn")}>
              Have an Account? Log In
            </button>
          </form>
        );
      case "logIn":
        return (
          <form onSubmit={handleSubmit}>
            <h2>Log In</h2>
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <button type="submit">Log In</button>
            <button type="button" onClick={() => setCurrentView("signUp")}>
              Create an Account
            </button>
          </form>
        );
      default:
        break;
    }
  };

  return (
    <div className={className}>
      {renderForm()}
      <button className="close-btn" onClick={onClose}>
        Close
      </button>
    </div>
  );
};

export default EntryPage;
