import axios from "axios";

// Define the API URL
const API_URL = `${process.env.REACT_APP_API_URL}/chat`; // Ensure this environment variable is set

// Function to send a message to the ChatGPT endpoint
export const sendMessageToChatGPT = async (message, language = "en") => {
  try {
    const response = await axios.post(API_URL, {
      message: message,
      language: language,
    });
    return response.data;
  } catch (error) {
    console.error("Error sending message to ChatGPT:", error);
    throw new Error("Failed to communicate with ChatGPT");
  }
};
