import React from "react";
import Header from "../components/Header";
import HeroSection from "../components/HeroSection";
import FeaturesSection from "../components/FeaturesSection";
import WhyUseSection from "../components/WhyUseSection";
import ContributeSection from "../components/ContributeSection";
import EntryPage from "../components/EntryPage";
import "../styles/HomePage.css"; // Ensure this path is correct

const HomePage = () => {
  const [showAuth, setShowAuth] = React.useState(false);

  const handleAuthToggle = () => {
    setShowAuth(!showAuth);
  };

  const handleOverlayClick = (e) => {
    // Close the popup if the overlay is clicked
    if (e.target.classList.contains("auth-popup-overlay")) {
      setShowAuth(false);
    }
  };

  return (
    <div className="homepage-container">
      <Header
        onLoginClick={handleAuthToggle}
        onSignupClick={handleAuthToggle}
      />
      {showAuth && (
        <div className="auth-popup-overlay" onClick={handleOverlayClick}>
          <EntryPage className="auth-popup" onClose={handleAuthToggle} />
        </div>
      )}
      <HeroSection />
      <FeaturesSection />
      <WhyUseSection />
      <ContributeSection />
    </div>
  );
};

export default HomePage;
