import React from "react";

const AuthForm = ({
  type,
  formData,
  handleChange,
  handleSubmit,
  toggleView,
}) => {
  return (
    <form onSubmit={handleSubmit}>
      <h2>{type === "signUp" ? "Sign Up" : "Log In"}</h2>
      {type === "signUp" && (
        <>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </>
      )}
      <label>Email:</label>
      <input
        type="email"
        name="email"
        value={formData.email}
        onChange={handleChange}
        required
      />
      <label>Password:</label>
      <input
        type="password"
        name="password"
        value={formData.password}
        onChange={handleChange}
        required
      />
      <button type="submit">{type === "signUp" ? "Sign Up" : "Log In"}</button>
      <button type="button" onClick={toggleView}>
        {type === "signUp" ? "Have an Account? Log In" : "Create an Account"}
      </button>
    </form>
  );
};

export default AuthForm;
