import React, { useState, useEffect, useRef } from "react";
import { sendMessageToChatGPT } from "../services/chatGPTService"; // Import the service
import "./ChatGPT.css";
import imgsend from "../images/sent.png";
import { Link } from "react-router-dom";

function ChatComponent() {
  const [messages, setMessages] = useState([]);
  const [userMessage, setUserMessage] = useState("");
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const chatMessagesRef = useRef(null);

  const sendMessage = async () => {
    if (userMessage.trim() !== "") {
      const newMessages = [
        ...messages,
        { sender: "You", message: userMessage },
      ];
      setMessages(newMessages);
      setUserMessage("");

      try {
        const response = await sendMessageToChatGPT(userMessage);
        setMessages((prevMessages) => [
          ...prevMessages,
          { sender: "Bot", message: response.message },
        ]);
      } catch (error) {
        setMessages((prevMessages) => [
          ...prevMessages,
          {
            sender: "Bot",
            message: "An error occurred. Please try again later.",
          },
        ]);
      }
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter") {
      sendMessage();
    }
  };

  useEffect(() => {
    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <div className="chat-app">
      <Link to="/MainPage">Homepage</Link>

      <div className={`sidebar ${sidebarOpen ? "open" : ""}`}>
        <button
          className="sidebar-toggle"
          onClick={() => setSidebarOpen(!sidebarOpen)}
        >
          {sidebarOpen ? "Close History" : "Open History"}
        </button>
        <div className="chat-history">
          <h3>Chat History</h3>
          <ul>
            {messages.map((msg, index) => (
              <li key={index}>
                <strong>{msg.sender}:</strong> {msg.message}
              </li>
            ))}
          </ul>
        </div>
      </div>

      <div className="chat-container">
        <div className="chat-messages" ref={chatMessagesRef}>
          {messages.map((msg, index) => (
            <div key={index} className="chat-message">
              <strong>{msg.sender}:</strong> {msg.message}
            </div>
          ))}
        </div>
        <div className="chat-input-container">
          <input
            type="text"
            className="chat-input"
            value={userMessage}
            placeholder="Type your message..."
            onChange={(e) => setUserMessage(e.target.value)}
            onKeyPress={handleKeyPress}
          />
          <button className="send-button" onClick={sendMessage}>
            <img src={imgsend} alt="Send" />
          </button>
        </div>
      </div>
    </div>
  );
}

export default ChatComponent;
